definition.script:
// Command-line options (taken from https://jenkins.io/doc/pipeline/examples/):
// --batch-mode : recommended in CI to inform maven to not run in interactive mode (less logs)
// -V : strongly recommended in CI, will display the JDK and Maven versions in use.
//      Very useful to be quickly sure the selected versions were the ones you think.
// -U : force maven to update snapshots each time (default : once an hour, makes no sense in CI).
// -Dsurefire.useFile=false : useful in CI. Displays test errors in the logs directly (instead of
//                            having to crawl the workspace files to see the cause).
// -e : Display stack-traces on failure

// Create release candidate branch
def repository = 'github.com/cmake-maven-project/cmake-maven-project.git'
// One can look up a project's profileId by running: mvn nexus-staging:rc-list-profiles -DserverId=sonatype-nexus-staging -DnexusUrl=https://oss.sonatype.org/
def stagingProfileId = '1a35794454094c'
def stagingRepositoryId

node("linux && amd64")
{
    // http://stackoverflow.com/a/33630506/14731
    withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'github', usernameVariable: 'GIT_USERNAME', passwordVariable: 'GIT_PASSWORD']])
    {
        def authenticatedRepository = "https://${GIT_USERNAME}:${GIT_PASSWORD}@${repository}"
        timeout(10)
        {
            stage("Checkout (linux-amd64)")
            {
                checkout([$class: 'GitSCM', branches: [[name: '*/master']], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'LocalBranch', localBranch: 'master'], [$class: 'CleanCheckout'], [$class: 'WipeWorkspace'], [$class: 'MessageExclusion', excludedMessage: '^\\[jenkins\\]']], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: "https://${repository}"]]])
            }
        }
        timeout(10)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "GIT_HOME=${ tool 'git' }", "PATH+MAVEN+GIT=${tool 'Maven'}/bin:${tool 'git'}:${env.JAVA_HOME}/bin"])
            {
                stage("Create release branch")
                {
                    // Check if tag already exists: http://stackoverflow.com/a/21116365/14731
                    // Commit without failure if nothing changed: http://stackoverflow.com/a/8123841/14731
                    def tagExists = sh(returnStdout: true, script: "git ls-remote https://${repository} release-${releaseVersion} | wc -l").toBoolean()
                    if (tagExists)
                        echo 'Tag release-' + releaseVersion + ' already exists'
                    else
                    {
                        echo 'Tagging release as release-' + releaseVersion
                        sh script: """git config user.email cowwoc@bbs.darktech.org
                            git config user.name "Gili Tzabari"
                            mvn --batch-mode -V -e versions:set -DnewVersion=${releaseVersion} -DgenerateBackupPoms=false
                            git add -u
                            git commit -m "[jenkins] Releasing version ${releaseVersion}"
                            git tag release-${releaseVersion}
                            git checkout master
                            mvn --batch-mode -V -e versions:set -DnewVersion=${nextVersion} -DgenerateBackupPoms=false
                            git add -u
                            git diff-index --quiet HEAD || git commit -m "[jenkins] Incrementing version to ${nextVersion}"
                            git push ${authenticatedRepository} master
                            git push ${authenticatedRepository} release-${releaseVersion}"""
                    }

                    // Open a new staging repository and capture its ID
                    def output = sh returnStdout: true, script: """git checkout tags/release-${releaseVersion}
                        mvn --batch-mode -U -e nexus-staging:rc-open -DserverId=sonatype-nexus-staging -DnexusUrl=https://oss.sonatype.org/ -DstagingProfileId=${stagingProfileId}"""
                    echo output
                    stagingRepositoryId = ( output =~ /Opened (comgooglecodecmake-maven-project-\d+)/ ) [0][1]
                }
            }
        }
    }
}

// Build and release from all platforms
try
{
    parallel linux_i386:
    {
        // Linux-i386 deploys the portable modules (which is convenient since it doesn't deploy platform-specific binaries).
        // Remaining platforms only deploy platform-specific binaries.
        node("linux && i386")
        {
            withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'github', usernameVariable: 'GIT_USERNAME', passwordVariable: 'GIT_PASSWORD']])
            {
                timeout(10)
                {
                    stage("Checkout (linux-i386)")
                    {
                        checkout([$class: 'GitSCM', branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'LocalBranch', localBranch: "release-${releaseVersion}"], [$class: 'CleanCheckout'], [$class: 'WipeWorkspace'], [$class: 'MessageExclusion', excludedMessage: '^\\[jenkins\\]']], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: "https://${repository}"]]])
                    }
                }
                timeout(15)
                {
                    withEnv(["JAVA_HOME=${ tool 'JAVA32_HOME' }", "GIT_HOME=${ tool 'git' }", "PATH+MAVEN+GIT=${tool 'Maven'}/bin:${tool 'git'}:${env.JAVA_HOME}/bin"])
                    {
                        stage("Build (linux-i386)")
                        {
                            sh script: 'mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Plinux-x86_32 clean install'
                        }
                        stage("Deploy (linux-i386)")
                        {
                            sh script: "mvn --batch-mode -e -DosDetection=false -Plinux-x86_32,release,empty-jar deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"
                            archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                        }
                    }
                }
            }
        }
    }, linux_amd64:
    {
        node("linux && amd64")
        {
            withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'github', usernameVariable: 'GIT_USERNAME', passwordVariable: 'GIT_PASSWORD']])
            {
                timeout(10)
                {
                    stage("Checkout (linux-amd64)")
                    {
                        checkout([$class: 'GitSCM', branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'LocalBranch', localBranch: "release-${releaseVersion}"], [$class: 'CleanCheckout'], [$class: 'WipeWorkspace'], [$class: 'MessageExclusion', excludedMessage: '^\\[jenkins\\]']], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: "https://${repository}"]]])
                    }
                }
                timeout(15)
                {
                    withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "GIT_HOME=${ tool 'git' }", "PATH+MAVEN+GIT=${tool 'Maven'}/bin:${tool 'git'}:${env.JAVA_HOME}/bin"])
                    {
                        stage("Build (linux-amd64)")
                        {
                            sh script: 'mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Plinux-x86_64 clean install'
                        }
                        stage("Deploy (linux-amd64)")
                        {
                            sh script: """cd cmake-binaries
                                mvn --batch-mode -e -DosDetection=false -Plinux-x86_64,release deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                            archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                        }
                    }
                }
            }
        }
    }, mac:
    {
        node("mac")
        {
            withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'github', usernameVariable: 'GIT_USERNAME', passwordVariable: 'GIT_PASSWORD']])
            {
                timeout(10)
                {
                    stage("Checkout (mac)")
                    {
                        checkout([$class: 'GitSCM', branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'LocalBranch', localBranch: "release-${releaseVersion}"], [$class: 'CleanCheckout'], [$class: 'WipeWorkspace'], [$class: 'MessageExclusion', excludedMessage: '^\\[jenkins\\]']], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: "https://${repository}"]]])
                    }
                }
                timeout(15)
                {
                    withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "GIT_HOME=${ tool 'git' }", "PATH+MAVEN+GIT=${tool 'Maven'}/bin:${tool 'git'}:${env.JAVA_HOME}/bin:/usr/local/bin"])
                    {
                        stage("Build (mac-amd64)")
                        {
                            sh script: 'mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Pmac-x86_64 clean install'
                        }
                        stage("Deploy (mac-amd64)")
                        {
                            sh script: """cd cmake-binaries
                                mvn --batch-mode -e -DosDetection=false -Pmac-x86_64,release deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                            archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                        }
                    }
                }
            }
        }
    }, windows:
    {
        node("windows && i386 && amd64")
        {
            withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'github', usernameVariable: 'GIT_USERNAME', passwordVariable: 'GIT_PASSWORD']])
            {
                timeout(10)
                {
                    stage("Checkout (windows)")
                    {
                        checkout([$class: 'GitSCM', branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'LocalBranch', localBranch: "release-${releaseVersion}"], [$class: 'CleanCheckout'], [$class: 'WipeWorkspace'], [$class: 'MessageExclusion', excludedMessage: '^\\[jenkins\\]']], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: "https://${repository}"]]])
                    }
                }
                timeout(15)
                {
                    withEnv(["JAVA_HOME=${ tool 'JAVA32_HOME' }", "GIT_HOME=${ tool 'git' }", "PATH+MAVEN+GIT=${tool 'Maven'}/bin;${tool 'git'};${env.JAVA_HOME}/bin"])
                    {
                        stage("Build (windows-i386)")
                        {
                            bat script: 'mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Pwindows-x86_32 clean install'
                        }
                        stage("Deploy (windows-i386)")
                        {
                            bat script: """cd cmake-binaries
                                mvn --batch-mode -e -DosDetection=false -Pwindows-x86_32,release deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                            archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                        }
                    }
                }
                timeout(15)
                {
                    withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "GIT_HOME=${ tool 'git' }", "PATH+MAVEN+GIT=${tool 'Maven'}/bin;${tool 'git'};${env.JAVA_HOME}/bin"])
                    {
                        stage("Build (windows-amd64)")
                        {
                            bat script: 'mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Pwindows-x86_64 clean install'
                        }
                        stage("Deploy (windows-amd64)")
                        {
                            bat script: """cd cmake-binaries
                                mvn --batch-mode -e -DosDetection=false -Pwindows-x86_64,release deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                            archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                        }
                    }
                }
            }
        }
    },
    failFast: true
}
catch (e)
{
    try
    {
        // Drop staging repository
        node("linux && amd64")
        {
            withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'github', usernameVariable: 'GIT_USERNAME', passwordVariable: 'GIT_PASSWORD']])
            {
                timeout(10)
                {
                    stage("Checkout (linux-amd64)")
                    {
                        checkout([$class: 'GitSCM', branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'LocalBranch', localBranch: "release-${releaseVersion}"], [$class: 'CleanCheckout'], [$class: 'WipeWorkspace'], [$class: 'MessageExclusion', excludedMessage: '^\\[jenkins\\]']], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: "https://${repository}"]]])
                    }
                }
                timeout(5)
                {
                    withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "GIT_HOME=${ tool 'git' }", "PATH+MAVEN+GIT=${tool 'Maven'}/bin:${tool 'git'}:${env.JAVA_HOME}/bin"])
                    {
                        sh script: "mvn --batch-mode -U -e nexus-staging:rc-drop -DserverId=sonatype-nexus-staging -DnexusUrl=https://oss.sonatype.org/ -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"
                    }
                }
            }
        }
    }
    catch (e2)
    {
        // Ignore and throw the original exception
    }
    throw e;
}

// Close the release
node("linux && amd64")
{
    withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'github', usernameVariable: 'GIT_USERNAME', passwordVariable: 'GIT_PASSWORD']])
    {
        timeout(10)
        {
            stage("Checkout (linux-amd64)")
            {
                checkout([$class: 'GitSCM', branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'LocalBranch', localBranch: "release-${releaseVersion}"], [$class: 'CleanCheckout'], [$class: 'WipeWorkspace'], [$class: 'MessageExclusion', excludedMessage: '^\\[jenkins\\]']], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: "https://${repository}"]]])
            }
        }
        timeout(10)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "GIT_HOME=${ tool 'git' }", "PATH+MAVEN+GIT=${tool 'Maven'}/bin:${tool 'git'}:${env.JAVA_HOME}/bin"])
            {
                stage("Close release")
                {
                    sh script: "mvn --batch-mode -U -e nexus-staging:rc-close -DserverId=sonatype-nexus-staging -DnexusUrl=https://oss.sonatype.org/ -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"
                }
            }
        }
    }
}
